import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  isOn: true,
  msg: "",
};
export const notiSlice = createSlice({
  name: "notification",
  initialState,
  reducers: {
    notifyVote(state, action) {
      return { ...state, msg: "You voted " + action.payload };
    },

    notifyNewAnec(state, action) {
      return { ...state, msg: "You added " + action.payload };
    },
  },
});

export const { notifyVote, notifyNewAnec } = notiSlice.actions;
