import { useDispatch } from "react-redux";
import { addNewAnec } from "../reducers/anecdoteReducer";
const AnecdoteForm = () => {
  const add = (e) => {
    e.preventDefault();
    useDispatch(addNewAnec(e.target.anec.value));
    e.target.anec.value = "";
  };

  return (
    <form onSubmit={add}>
      <div>
        <input name="anec" />
      </div>
      <button>create</button>
    </form>
  );
};
