import { useSelector, useDispatch } from "react-redux";
import { addNewAnec, voting } from "./reducers/anecdoteReducer";

const App = () => {
  const anecdotes = useSelector((state) => state);
  const dispatch = useDispatch();

  const vote = (id) => {
    console.log("vote", id);
    dispatch(voting(id));
  };

  const add = (e) => {
    e.preventDefault();
    dispatch(addNewAnec(e.target.anec.value));
    e.target.anec.value = "";
  };

  return (
    <div>
      <h2>Anecdotes</h2>
      {anecdotes
        .sort((a, b) => b.votes - a.votes)
        .map((anecdote) => (
          <div key={anecdote.id}>
            <div>{anecdote.content}</div>
            <div>
              has {anecdote.votes}
              <button onClick={() => vote(anecdote.id)}>vote</button>
            </div>
          </div>
        ))}
      <h2>create new</h2>
      <form onSubmit={add}>
        <div>
          <input name="anec" />
        </div>
        <button>create</button>
      </form>
    </div>
  );
};

export default App;
