import { useDispatch, useSelector } from "react-redux";
import { voting } from "../reducers/anecdoteReducer";
import { notifyMsg, turnOff, turnOn } from "../reducers/notificationReducer";

const AnecdoteList = () => {
  const dispatch = useDispatch();

  const anecdotes = [...useSelector((state) => state.anecdote)];

  const vote = (id) => {
    console.log("vote", id);
    dispatch(voting(id));
    dispatch(
      notifyMsg(
        "You have voted " + anecdotes.find((anec) => anec.id === id).content
      )
    );
    dispatch(turnOn());
    setTimeout(() => {
      dispatch(turnOff());
    }, 5000);
  };

  //console.log(anecdotes);

  return anecdotes
    .sort((a, b) => b.votes - a.votes)
    .map((anecdote) => (
      <div key={anecdote.id}>
        <div>{anecdote.content}</div>
        <div>
          has {anecdote.votes}
          <button onClick={() => vote(anecdote.id)}>vote</button>
        </div>
      </div>
    ));
};

export default AnecdoteList;
